mapper.select:
SELECT COUNT(*) FROM goods_jjim gj,goods_list gl
WHERE gj.pno=gl.no
  AND pno=#{pno} 
  AND id = #{id}

mapper.delete:
DELETE FROM goods_jjim gj,goods_list gl
WHERE gj.pno=gl.no
  AND pno = #{pno} 
  AND id = #{id}

