mapper.update:
UPDATE reply SET
msg=#{msg}
WHERE no=#{no}

mapper.select:
SELECT group_id,group_step,group_tab
FROM homet_board_reply
WHERE no=#{no}

mapper.update:
UPDATE reply SET
group_step=group_step+1
WHERE group_id=#{group_id} AND group_step>#{group_step}

UPDATE homet_board_reply SET
depth=depth+1
WHERE no=#{no}

UPDATE homet_board_reply SET
msg='관리자가 삭제한 댓글입니다'
WHERE no=#{no}

UPDATE homet_board_reply SET
depth=depth-1
WHERE no=#{no}
